import React, { Component } from 'react'
import './App.css'

import Price from './ui/Price'
import priceInfo from './ui/priceInfo';
import { tsUndefinedKeyword } from '@babel/types';


export class App extends Component {

  state={
    month:undefined,
    year:undefined
  }

getPrice = async(e)=>{

  e.preventDefault()

    const month = e.target.elements.month.value
    const year = e.target.elements.year.value

  const apiCall = await fetch(`https://yfarmapi.herokuapp.com/price/v1?month=${month}&year=${year}`)
  const data = await apiCall.json()
  console.log(data)
}



  render() {
    return (
      <div>
        {/* <Price/> */}
          <Price getPrice = {this.getPrice} />   
          <priceInfo Month= {this.month}
                     Year = {this.year}/>
      </div>
    )

  }
}

export default App
